mapper.select:
select * from s_teacher


        name = #{teacher.name}


        and clazz_id = #{teacher.clazz_id}


        and id = #{teacher.id}


limit #{page},#{limit}

select count(*) as total from s_teacher


        name =#{name}


        and clazz_id = #{clazz_id}


        and id = #{id}

mapper.update:
update s_teacher
set name = #{name}, sex = #{sex}, tposition = #{tposition}, salary=#{salary}, clazz_id=#{clazz_id}
where id= #{id}

mapper.delete:
delete from s_teacher

    where id in

        #{id}
